function getFilms(page: number, query?: string) {
  let rawData = films
  if (query) {
    rawData = films.filter(i => i.name.toLowerCase().startsWith(query.toLowerCase()))
  }
  const maxPage = Math.ceil(rawData.length / limit)
  return {
    items: rawData.slice(...getParams(page, limit)),
    maxPage
  }
}

function getFilmById(id: number) {
  return films.find((i) => i.id === id)
}

function getParams(page: number, limit: number) {
  if (page === 0 || page === 1) {
    return [(page - 1) * (limit - 1), page * limit]
  }
  return [(page - 1) * (limit), page * limit]
}
const limit = 8

export const api = {
  getFilms,
  getFilmById
}
export interface IFilm {
  id: number,
  name: string,
  rating: IRate,
  year: number,
  genres: Array<{ name: string }>,
  countries: Array<{ name: string }>,
  persons: Array<IPerson>,
  shortDescription: string,
  description: string,
  backdrop: {
    url: string,
    previewUrl: string,
  },
  facts: Array<{ value: string }>,
}
export type IRate = {
  kp: number,
  imdb: number,
}
export interface IPerson {
  id: number,
  photo: string,
  name: string,
  enName: string,
  description: string | null,
  profession: string,
  enProfession: string,
}